package main; import "fmt"; func main(){ var pizzaD int; var pizzaN int; var pizzaS float64; fmt.Println("Діаметр піцци (циферки після назви):"); fmt.Scan(&pizzaD); var pizzaR float64; pizzaR = float64(pizzaD) / 2; fmt.Println("Скільки у вас піцц: "); fmt.Scan(&pizzaN); switch pizzaN{case 1: pizzaS = pizzaR*pizzaR*3.14; case 2: pizzaS = pizzaR*pizzaR*3.14*2; case 3: pizzaS = pizzaR*pizzaR*3.14*3; case 4: pizzaS = pizzaR*pizzaR*3.14*4; case 5: pizzaS = pizzaR*pizzaR*3.14*5; case 6: pizzaS = pizzaR*pizzaR*3.14*6; case 7: pizzaS = pizzaR*pizzaR*3.14*7; case 8: pizzaS = pizzaR*pizzaR*3.14*8; case 9: pizzaS = pizzaR*pizzaR*3.14*9; case 10: pizzaS = pizzaR*pizzaR*3.14*10; case 11: pizzaS = pizzaR*pizzaR*3.14*11; case 12: pizzaS = pizzaR*pizzaR*3.14*12; default: fmt.Println("В мене стільки піцц не вміщаються в умови конкурсу, вам то куди стільки?")}; if pizzaS != 0 {fmt.Printf("Загальна площа піцц... *барабана дріб* %v квадратних сантиметрів!", pizzaS)}}